import React from 'react';
import Button from './components/button'

interface NavigationProps {
  onAdvance?: () => void;
  onPrevious?: () => void;
  disableAdvance?: boolean;
  disablePrevious?: boolean;
  showAdvanceButton?: boolean;
  showPreviousButton?: boolean;
  previousButtonText?: string;
  advanceButtonText?: string;
}

const Navigation: React.FC<NavigationProps> = ({
  onAdvance,
  onPrevious,
  disableAdvance,
  disablePrevious,
  showAdvanceButton = true,
  showPreviousButton = true,
  previousButtonText = 'Previous',
  advanceButtonText = 'Next',
}) => {
  return (
    <div>
      {showPreviousButton && onPrevious && (
        <button
          className="text-md p-2 border-lg mr-2 border-2"
          onClick={onPrevious}
          disabled={disablePrevious}>
          value= {previousButtonText}
        </button>
      )}
      {showAdvanceButton && onAdvance && (
        <Button
          className="text-md p-2"
          onClick={onAdvance}
          disabled={disableAdvance}
          label={advanceButtonText}>
        </Button>
      )}
      <Button label="Primary Button" variant="primary" onClick={onAdvance} />
      <Button label="Secondary Button" variant="secondary" size="small" className="mt-2" />
      <Button label="Danger Button" variant="danger" size="large" className="mt-2" />

    </div>
  );
};

export default Navigation;
