import React from 'react';

interface ButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  label: string;           // Button text
  variant?: 'primary' | 'secondary' | 'danger'; // Optional: Button variant
  size?: 'small' | 'medium' | 'large'; // Optional: Button size
}

const Button: React.FC<ButtonProps> = ({
  label,
  variant = 'primary',
  size = 'medium',
  className,
  disabled = false,
  ...props
}) => {
  // Define base styles
  const baseStyles = 'rounded-full border transition duration-300 ease-in-out focus:outline-none focus:ring-2';

  // Define styles based on variant
  const variantStyles = {
    primary: 'bg-gray-500 border-gray-600 hover:bg-gray-600 focus:ring-gray-700',
    secondary: 'bg-gray-500 border-gray-600 text-white hover:bg-gray-600 focus:ring-gray-500',
    danger: 'bg-red-500 border-red-600 text-white hover:bg-red-600 focus:ring-red-500',
  };

  // Define styles based on size
  const sizeStyles = {
    small: 'py-1 px-2 text-sm',
    medium: 'py-2 px-4 text-lg',
    large: 'py-3 px-6 text-xl',
  };

  // Styles for the disabled state
  const disabledStyles = 'bg-gray-400 border-gray-500 text-gray-200 cursor-not-allowed opacity-50';

  // Combine all styles
  const combinedStyles = `${baseStyles} ${disabled ? disabledStyles : variantStyles[variant]} ${sizeStyles[size]} ${className}`;

  return (
    <button
      className={`${baseStyles} ${variantStyles[variant]} ${sizeStyles[size]} ${className}`}
      {...props} // Spread any additional props (like onClick, disabled, etc.)
    >
      {label}
    </button>
  );
};

export default Button;
