import React from 'react';

interface NavigationProps {
  onAdvance?: () => void;
  onPrevious?: () => void;
  disableAdvance?: boolean;
  disablePrevious?: boolean;
  showAdvanceButton?: boolean;
  showPreviousButton?: boolean;
  previousButtonText?: string;
  advanceButtonText?: string;
}

const Navigation: React.FC<NavigationProps> = ({
  onAdvance,
  onPrevious,
  disableAdvance,
  disablePrevious,
  showAdvanceButton = true,
  showPreviousButton = true,
  previousButtonText = 'Previous',
  advanceButtonText = 'Next',
}) => {
  return (
    <div>
      {showPreviousButton && onPrevious && (
        <button
        className="text-md p-2 border-lg mr-2 text-red-500"
          onClick={onPrevious}
          disabled={disablePrevious}>
          {previousButtonText}
        </button>
      )}
      {showAdvanceButton && onAdvance && (
        <button
        className="text-md p-2 border-lg"
          onClick={onAdvance}
          disabled={disableAdvance}>
          {advanceButtonText}
        </button>
      )}
    </div>
  );
};

export default Navigation;
